import { Component, Input, OnInit } from '@angular/core';
import Chart from 'chart.js/auto';
import { CData } from '../Data';
import { HttpClient } from "@angular/common/http";


@Component({
  selector: 'app-charts',
  templateUrl: './charts.component.html',
  styleUrls: ['./charts.component.css']
})

export class ChartsComponent implements OnInit {
  public chart: any;
  public dataArray: Array<CData> = [];
  public monthsArray: String[] = [];

  constructor(private http: HttpClient){
    this.http.get('assets/formatted_data.csv', {responseType: 'text'})
    .subscribe(
        data => {
            let csvToRowArray = data.split("\n");
            for (let index = 1; index < csvToRowArray.length - 1; index++) {
              let row = csvToRowArray[index].split(",");
              this.dataArray.push(new CData( parseInt( row[0]), row[1], row[2], parseInt(row[3])));
              this.monthsArray.indexOf(row[2]) === -1 ? this.monthsArray.push(row[2]) : console.log("This item already exists");
            }
            this.monthsJson = JSON.stringify(this.monthsArray);
            console.log(this.monthsJson);

        },
        error => {
            console.log(error);
        }
    );
  }

  ngOnInit(): void {
    this.createChart(this.dataArray);


    console.log(this.monthsJson)


  }

  createChart(dataArray: CData[]){
    var months = [];




    this.chart = new Chart("MyChart", {
      type: 'bar', //this denotes tha type of chart

      data: {// values on X-Axis
        labels: [this.monthsJson], 
	       datasets: [
          {
            label: "Sales",
            data: ['467','576', '572', '79', '92',
								 '574', '573', '576'],
            backgroundColor: 'blue'
          },
          {
            label: "Profit",
            data: ['542', '542', '536', '327', '17',
									 '0.00', '538', '541'],
            backgroundColor: 'limegreen'
          }  
        ]
      },
      options: {
        aspectRatio:2.5
      }

    });
  }
}

