import { Component, OnInit } from '@angular/core';
import { CData } from '../Data'
import { HttpClient } from "@angular/common/http";

@Component({
  selector: 'app-countries',
  templateUrl: './countries.component.html',
  styleUrls: ['./countries.component.css']
})
export class CountriesComponent implements OnInit {
  displayedColumns: string[] = ['Country', 'Month-Year', 'Value'];
  dataSource = [];
  public dataArray: CData[] = [];

  constructor(private http: HttpClient){
    this.http.get('assets/formatted_data.csv', {responseType: 'text'})
    .subscribe(
        data => {
            let csvToRowArray = data.split("\n");
            for (let index = 1; index < csvToRowArray.length - 1; index++) {
              let row = csvToRowArray[index].split(",");
              this.dataArray.push(new CData( parseInt( row[0]), row[1], row[2], parseInt(row[3])));
            }
            console.log(this.dataArray);
        },
        error => {
            console.log(error);
        }
    );
  }

  // country: CData = {
  //   id: 1,
  //   country: 'KSA',
  //   month: 'Jul-91',
  //   value: 10000
  // }


  ngOnInit(): void {

  }

}


<!-- Charts -->
<div class="container"></div>
<app-charts></app-charts>
<h2>Table</h2>
  <p>Here are some links to help you get started:</p>

    <table id="datat" class="table table-responsive">
        <thead>
          <tr>
            <th>ID</th>
            <th>Country</th>
            <th>Month-Year</th>
            <th>Value</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let data of dataArray">
            <td>{{data.id}}</td>
            <td>{{data.country}}</td>
            <td>{{data.month}}</td>
            <td>{{data.value}}</td>
          </tr>
        </tbody>
      </table>
